using System;
using System.Collections.Generic;
using System.Text;

namespace Vendor
{
    public class Stereo
    {
        public void on()
        {
            Console.WriteLine("Stereo System is on");
        }
        public void off()
        {
            Console.WriteLine("Stereo is off");
        }
        public void setCd()
        {
            Console.WriteLine("Set the CD in the Stereo");
        }
        public void setRadio()
        {
            Console.WriteLine("Set the Radio in the Stereo");
        }
        public void setVolume()
        {
            Console.WriteLine("Set the Volume in the Stereo");
        }
    }
}
